import React, {useEffect, useState} from 'react'; // ES6 js
import {Link, NavLink} from 'react-router-dom';

function Nav() {
    const location = useLocation();
    const [url, setUrl] = useState(null);
    useEffect(() => {
        setUrl(location.pathname);
      }, [location]);
    return(
        <nav class="navbar navbar-expand-lg navbar-dark bg-dark top">
            <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navMainMenu" aria-controls="navMainMenu" aria-expanded="false" aria-label="Toggle navigation">
                <span class="navbar-toggler-icon"></span>
            </button>
            <div id="navMainMenu" class="navbar-collapse collapse">
                <div class="navbar-nav ml-auto">


                    <Link to="/" className={"nav-item nav-link" + (url === "/" ?" active" : "")}>Home</Link>
                    <Link to="/snake" className={"nav-item nav-link" + (url === "/snake" ?" active" : "")}>Snake</Link>
                </div>
            </div>
        </nav>
    );
}

export default Nav;
